convert.addEventListener("click", function() {
	var map:ByteArray = Base64.decodeToByteArray(input.text);
	map.uncompress();
	XML.prettyPrinting = true;
	XML.prettyIndent = 2;
	XML.ignoreWhitespace = false;
	XML.ignoreComments = false;
	output.text = new XML(map.toString()).toXMLString();
});

encode.addEventListener("click", function() {
	XML.prettyPrinting = false;
	XML.prettyIndent = 0;
	XML.ignoreWhitespace = true;
	XML.ignoreComments = true;
	output.text = "<?xml version=\"1.0\" encoding=\"iso-8859-1\"?>\n" + new XML(output.text).toXMLString();

	var map:ByteArray = new ByteArray();
	map.writeUTFBytes(output.text);
	map.compress();

	input.text = Base64.encodeByteArray(map);
});
